import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    // The URL for your Google Apps Script
    const scriptUrl = 'https://script.google.com/macros/s/AKfycbzbOgX2g7Mys3R3MhnP3UTW-Ax_1qj_Dkk8flIv3DzIWwktSjdnULUuMNW8vveDgG8_/exec';

    fetch(scriptUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'text/plain', // Required for Apps Script POST requests
      },
      body: JSON.stringify({ email, password }),
    })
    .then(res => res.json())
    .then(data => {
      setIsLoading(false);
      if (data.status === 'success') {
        // On successful login, save the email and navigate to the dashboard
        localStorage.setItem('userEmail', data.email);
        navigate('/dashboard');
      } else {
        // On failed login, show an error message
        setError(data.message || 'Invalid credentials.');
      }
    })
    .catch(err => {
      setIsLoading(false);
      setError('An error occurred during login. Please try again.');
      console.error(err);
    });
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '400px', margin: 'auto' }}>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div style={{ marginBottom: '1rem' }}>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ width: '100%', padding: '0.5rem' }}
          />
        </div>
        <div style={{ marginBottom: '1rem' }}>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: '100%', padding: '0.5rem' }}
          />
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit" disabled={isLoading} style={{ padding: '0.75rem 1.5rem' }}>
          {isLoading ? 'Logging in...' : 'Login'}
        </button>
      </form>
    </div>
  );
};

export default Login;
